import axios from "axios";
import { useState, useEffect } from "react";
// import date picker
import { DateRangePicker } from 'react-date-range';
import 'react-date-range/dist/styles.css'; // main css file
import 'react-date-range/dist/theme/default.css'; // theme css file
import { addDays } from 'date-fns';
import SearchSunriseSunset from './components/SearchSunriseSunset';

const baseURL = "https://data.weather.gov.hk/weatherAPI/opendata/opendata.php?dataType=SRS&year=2021&rformat=json";

export default function SunriseSunset() {
  const [sunrise, setSunrise] = useState(null);

  // Get Request with filter - SunriseSunset
  useEffect(() => {
    axios.get(baseURL).then((response) => {
        setSunrise(response.data.data);
      console.log(`effect`,response.data.data);
    });
  }, []);

  const [dateRange, setdateRange] = useState([
    {
      startDate: new Date(),
      endDate: addDays(new Date(), 7),
    }
  ]);

  console.log(`dateRange`,dateRange);
  return(
      <>
        <DateRangePicker
            onChange={item => setdateRange([item.selection])}
            showSelectionPreview={true}
            moveRangeOnFirstSelection={false}
            minDate={new Date("01-01-2018")}
            maxDate={new Date("12-31-2023")}
            months={2}
            ranges={dateRange}
            direction="horizontal"
        />
        <SearchSunriseSunset dateRange={dateRange} />
      </>
    )

