import axios from "axios";
import { useState, useEffect } from "react";
// import {
//     Chart as ChartJS,
//     CategoryScale,
//     LinearScale,
//     PointElement,
//     LineElement,
//     Title,
//     Tooltip,
//     Legend,
//   } from 'chart.js';
//   import { Line } from 'react-chartjs-2';
import { ResponsiveLine } from '@nivo/line'

export default function SearchSunriseSunset(props) {
    const [yearData, setYearData] = useState("")

    useEffect(() => {
        let year = Object.values(props);
        let API = `https://data.weather.gov.hk/weatherAPI/opendata/opendata.php?dataType=SRS&year=${year}&rformat=json`;
            axios.get(API, {mode:'cors'}).then((response) => {
                setYearData(response.data.data);
            });
        }, [props]);

        // let sunriseStat = yearData.jsonarray.map(function(e) {
        //     return e[0][1]
        // })

        // // push all dates in a array
        // let dateLabel = [];
        // useEffect(() => {
        //     for (let i = 0; i < yearData.length; i++) {
        //         dateLabel.push({"date":yearData?.[i]?.[0],"time":yearData?.[i]?.[1]})
        //     };
        //     console.log(`dateLabel`,dateLabel)
        // }, [yearData]);

        // push all sunrise time in a array 
        let sunriseStat = [];
        useEffect(() => {
            for (let i = 0; i < yearData.length; i++) {
                sunriseStat.push({"date":yearData?.[i]?.[0],"time":yearData?.[i]?.[1]})
            };
            console.log(`sunriseStat`,sunriseStat)
        }, [yearData]);

        // push all sunrise time in a array 
        let sunsetStat = [];
        useEffect(() => {
            for (let i = 0; i < yearData.length; i++) {
                sunsetStat.push({"date":yearData?.[i]?.[0],"time":yearData?.[i]?.[2]})
            };
            console.log(`sunsetStat`,sunsetStat)
        }, [yearData]);


        // ChartJS.register(
        //     CategoryScale,
        //     LinearScale,
        //     PointElement,
        //     LineElement,
        //     Title,
        //     Tooltip,
        //     Legend
        //   );

    //    const options = {
    //         responsive: true,
    //         plugins: {
    //           legend: {
    //             position: 'top',
    //           },
    //           title: {
    //             display: true,
    //             text: 'Sunriseeeee',
    //           },
    //         },
    //         }



    //     const data = {
    //     dateLabel,
    //     datasets: [
    //         {
    //         label: 'Sunrise',
    //         data: sunriseStat,
    //         borderColor: 'rgb(255, 99, 132)',
    //         backgroundColor: 'rgba(255, 99, 132, 0.5)',
    //         },
    //         {
    //         label: 'Sunset',
    //         data: sunsetStat,
    //         borderColor: 'rgb(53, 162, 235)',
    //         backgroundColor: 'rgba(53, 162, 235, 0.5)',
    //         },
    //     ]
    // };


         const data = [
                {
                  "id": "Sunrise",
                  "color": "hsl(268, 70%, 50%)",
                  "data": sunriseStat
                },
                {
                    "id": "Sunset",
                    "color": "hsl(331, 70%, 50%)",
                    "data": sunsetStat
                  },
            ]

        return (
        <>
            {/* <Line options={options} data={data} />  */}
            <ResponsiveLine
        data={data}
        margin={{ top: 50, right: 110, bottom: 50, left: 60 }}
        xScale={{
            type: 'time',
            format: '%Y-%m-%d',
            useUTC: false,
            precision: 'day',
        }}
        yScale={{
            type: 'linear',
            min: 'auto',
            max: 'auto',
            stacked: true,
            reverse: false
        }}
        yFormat=" >-.2f"
        axisTop={null}
        axisRight={null}
        axisBottom={{
            orient: 'bottom',
            tickSize: 5,
            tickPadding: 5,
            tickRotation: 0,
            legend: 'Date',
            legendOffset: 36,
            legendPosition: 'middle'
        }}
        axisLeft={{
            orient: 'left',
            tickSize: 5,
            tickPadding: 5,
            tickRotation: 0,
            legend: 'Time',
            legendOffset: -40,
            legendPosition: 'middle'
        }}
        pointSize={10}
        pointColor={{ theme: 'background' }}
        pointBorderWidth={2}
        pointBorderColor={{ from: 'serieColor' }}
        pointLabelYOffset={-12}
        useMesh={true}
        legends={[
            {
                anchor: 'bottom-right',
                direction: 'column',
                justify: false,
                translateX: 100,
                translateY: 0,
                itemsSpacing: 0,
                itemDirection: 'left-to-right',
                itemWidth: 80,
                itemHeight: 20,
                itemOpacity: 0.75,
                symbolSize: 12,
                symbolShape: 'circle',
                symbolBorderColor: 'rgba(0, 0, 0, .5)',
                effects: [
                    {
                        on: 'hover',
                        style: {
                            itemBackground: 'rgba(0, 0, 0, .03)',
                            itemOpacity: 1
                        }
                    }
                ]
            }
        ]}
    />   
        </>)

}
