import axios from "axios";
import { useState, useEffect } from "react";
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
  } from 'chart.js';
  import { Line } from 'react-chartjs-2';

export default function SearchSunriseSunset(props) {
    const [yearData, setYearData] = useState("")

    useEffect(() => {
        let year = Object.values(props);
        let API = `https://data.weather.gov.hk/weatherAPI/opendata/opendata.php?dataType=SRS&year=${year}&rformat=json`;
            axios.get(API, {mode:'cors'}).then((response) => {
                setYearData(response.data.data);
            });
        }, [props]);

        // let sunriseStat = yearData.jsonarray.map(function(e) {
        //     return e[0][1]
        // })
        console.log(`sunriseStat`,yearData)

        useEffect(() => {
            let dateLabel = [];
            for (let i = 0; i < yearData.length; i++) {
                dateLabel.push(yearData?.[i]?.[0])
            };
            console.log(`dateLabel`,dateLabel)
        }, [yearData]);


        // let sunriseData = yearData.data.map(e => ({
        //     data: e,
        //     time: "label"
        // }));
        // console.log(`sunriseData`,sunriseData);



        ChartJS.register(
            CategoryScale,
            LinearScale,
            PointElement,
            LineElement,
            Title,
            Tooltip,
            Legend
          );

       const options = {
            responsive: true,
            plugins: {
              legend: {
                position: 'top',
              },
              title: {
                display: true,
                text: 'Sunriseeeee',
              },

            },
          }

        //   console.log(`yearDatafield`,yearData.data[0])
          const labels = yearData.fields;

        const data = {
        labels,
        datasets: [
            {
            label: 'Sunrise',
            data: [12, 19, 3, 5, 2, 3],
            borderColor: 'rgb(255, 99, 132)',
            backgroundColor: 'rgba(255, 99, 132, 0.5)',
            },
            {
            label: 'Sunset',
            data: [14, 9, 3, 3, 2, 3],
            borderColor: 'rgb(53, 162, 235)',
            backgroundColor: 'rgba(53, 162, 235, 0.5)',
            },
        ],
        };
        return (
        <>
            <Line options={options} data={data} />    
        </>)

}
