import axios from "axios";
import { useState, useEffect } from "react";


export default function SearchSunriseSunset(props) {

    console.log(Object.values(props))
    const [yearData, setYearData] = useState("")


    useEffect(() => {
        let year = Object.values(props);
        let API = `https://data.weather.gov.hk/weatherAPI/opendata/opendata.php?dataType=SRS&year=${year}&rformat=json`;
            axios.get(API, {mode:'cors'}).then((response) => {
                setYearData(response.data.data);
            });
        }, [props]);

        console.log(`yearData`,yearData)
        // var sunriseStat = yearData.jsonarray.map(function(e) {
        //     return e.name;
        //  });

        let data = [
            {
              id: "Sunrise",
              "data": [
                {
                  "x": "plane",
                  "y": 131
                },
                {
                  "x": "helicopter",
                  "y": 177
                },
                {
                  "x": "boat",
                  "y": 236
                },
                {
                  "x": "train",
                  "y": 120
                },
                {
                  "x": "subway",
                  "y": 88
                },
                {
                  "x": "bus",
                  "y": 147
                },
                {
                  "x": "car",
                  "y": 210
                },
                {
                  "x": "moto",
                  "y": 52
                },
                {
                  "x": "bicycle",
                  "y": 98
                },
                {
                  "x": "horse",
                  "y": 103
                },
                {
                  "x": "skateboard",
                  "y": 81
                },
                {
                  "x": "others",
                  "y": 156
                }
              ]
            },
            {
              "id": "france",
              "data": [
                {
                  "x": "plane",
                  "y": 49
                },
                {
                  "x": "helicopter",
                  "y": 54
                },
                {
                  "x": "boat",
                  "y": 54
                },
                {
                  "x": "train",
                  "y": 35
                },
                {
                  "x": "subway",
                  "y": 231
                },
                {
                  "x": "bus",
                  "y": 218
                },
                {
                  "x": "car",
                  "y": 167
                },
                {
                  "x": "moto",
                  "y": 170
                },
                {
                  "x": "bicycle",
                  "y": 152
                },
                {
                  "x": "horse",
                  "y": 272
                },
                {
                  "x": "skateboard",
                  "y": 48
                },
                {
                  "x": "others",
                  "y": 291
                }
              ]
            }
          ]

        return (<>
            <ResponsiveLine
            data={data}
            margin={{ top: 50, right: 110, bottom: 50, left: 60 }}
            xScale={{
            type: 'time',
            format: '%Y-%m-%d',
            useUTC: false,
            precision: 'day',
            }}
            xFormat="time:%Y-%m-%d"           
             yScale={{
                type: 'linear',
                min: 'auto',
                max: 'auto',
                stacked: true,
                reverse: false
            }}
            yFormat=" >-.2f"
            curve="natural"
            axisTop={null}
            axisRight={null}
            axisBottom={{
                orient: 'bottom',
                tickSize: 5,
                tickPadding: 5,
                tickRotation: 0,
                legend: 'transportation',
                legendOffset: 36,
                legendPosition: 'middle'
            }}
            axisLeft={{
                orient: 'left',
                tickSize: 5,
                tickPadding: 5,
                tickRotation: 0,
                legend: 'count',
                legendOffset: -40,
                legendPosition: 'middle'
            }}
            pointSize={10}
            pointColor={{ theme: 'background' }}
            pointBorderWidth={2}
            pointBorderColor={{ from: 'serieColor' }}
            pointLabelYOffset={-12}
            useMesh={true}
            legends={[
                {
                    anchor: 'bottom-right',
                    direction: 'column',
                    justify: false,
                    translateX: 100,
                    translateY: 0,
                    itemsSpacing: 0,
                    itemDirection: 'left-to-right',
                    itemWidth: 80,
                    itemHeight: 20,
                    itemOpacity: 0.75,
                    symbolSize: 12,
                    symbolShape: 'circle',
                    symbolBorderColor: 'rgba(0, 0, 0, .5)',
                    effects: [
                        {
                            on: 'hover',
                            style: {
                                itemBackground: 'rgba(0, 0, 0, .03)',
                                itemOpacity: 1
                            }
                        }
                    ]
                }
            ]}
        />
    </>)

}
